import React, { useState, useEffect } from 'react';
import DatePicker from 'react-datepicker';
import 'react-datepicker/dist/react-datepicker.css';
import { useNavigate } from 'react-router-dom';
import Layout from './Layout';
import LeaveRequestPopup from './LeaveRequestPopup';
import './LeaveManagement.css';

interface UserData {
  id: number;
  name: string;
  email: string;
  role_id: number;
  department_id: number;
}

interface LeaveManagementProps {
  userData: UserData;
  onLogout: () => void;
}

interface LeaveRequest {
  userId: number;
  startDate: Date;
  endDate: Date;
  leaveType: string;
}

const LeaveManagement: React.FC<LeaveManagementProps> = ({ userData, onLogout }) => {
  const navigate = useNavigate();
  const [selectedDate, setSelectedDate] = useState<Date | null>(new Date());
  const [leaveData, setLeaveData] = useState<any>(null);
  const [isPopupOpen, setIsPopupOpen] = useState(false);
  const [leaveRequests, setLeaveRequests] = useState<LeaveRequest[]>([]);
  const [users, setUsers] = useState<UserData[]>([]);

  useEffect(() => {
    if (!userData) {
      navigate('/');
    }
    // Fetch users and leave requests
    fetchUsers();
    fetchLeaveRequests();
  }, [userData, navigate]);

  const fetchUsers = async () => {
    try {
      const response = await fetch('http://localhost:8000/users');
      const data = await response.json();
      setUsers(data);
    } catch (error) {
      console.error('Error fetching users:', error);
    }
  };

  const fetchLeaveRequests = async () => {
    try {
      const response = await fetch('http://localhost:8000/leave-requests');
      const data = await response.json();
      setLeaveRequests(data);
    } catch (error) {
      console.error('Error fetching leave requests:', error);
    }
  };

  const handleDateChange = (date: Date | null) => {
    setSelectedDate(date);
    if (date) {
      // Here you would typically fetch leave data for the selected date
      console.log('Fetching leave data for:', date);
    }
  };

  const handleNewLeave = () => {
    setIsPopupOpen(true);
  };

  const handleClosePopup = () => {
    setIsPopupOpen(false);
  };

  const handleSubmitLeave = async (leaveData: LeaveRequest) => {
    try {
      const response = await fetch('http://localhost:8000/leave-requests', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(leaveData),
      });
      if (response.ok) {
        fetchLeaveRequests();
      }
    } catch (error) {
      console.error('Error submitting leave request:', error);
    }
  };

  const isDateInLeaveRange = (date: Date) => {
    return leaveRequests.some(request => {
      const start = new Date(request.startDate);
      const end = new Date(request.endDate);
      return date >= start && date <= end;
    });
  };

  const dayClassName = (date: Date) => {
    return isDateInLeaveRange(date) ? 'leave-day' : '';
  };

  return (
    <Layout userData={userData} onLogout={onLogout}>
      <div className="leave-container">
        <div className="leave-header">
          <h1>Leave Management</h1>
          <button className="new-leave-button" onClick={handleNewLeave}>
            <svg className="button-icon" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
              <path d="M12 4v16m8-8H4" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
            </svg>
            New Leave
          </button>
        </div>
        <div className="leave-content">
          <div className="date-picker-container">
            <DatePicker
              selected={selectedDate}
              onChange={handleDateChange}
              inline
              calendarClassName="custom-calendar"
              dayClassName={dayClassName}
            />
          </div>
          <div className="leave-details">
            {leaveData ? (
              <div className="leave-info">
                <h2>Leave Details</h2>
                <p>Selected Date: {selectedDate?.toLocaleDateString()}</p>
              </div>
            ) : (
              <div className="no-leave-data">
                <p>No leave data available for the selected date</p>
              </div>
            )}
          </div>
        </div>
      </div>

      <LeaveRequestPopup
        isOpen={isPopupOpen}
        onClose={handleClosePopup}
        onSubmit={handleSubmitLeave}
        currentUser={userData}
        users={users}
      />
    </Layout>
  );
};

export default LeaveManagement;
