import React from 'react';
import { useNavigate } from 'react-router-dom';

interface UserData {
  name: string;
  email: string;
  role_id: number;
}

interface WelcomeProps {
  onLogout: () => void;
  userData: UserData | null;
}

const Welcome: React.FC<WelcomeProps> = ({ onLogout, userData }) => {
  const navigate = useNavigate();

  const handleLogout = () => {
    onLogout();
    navigate('/');
  };

  const getRoleName = (roleId: number) => {
    switch (roleId) {
      case 1:
        return 'Admin';
      case 2:
        return 'HR Manager';
      case 3:
        return 'Employee';
      default:
        return 'Unknown';
    }
  };

  if (!userData) {
    return null;
  }

  return (
    <div className="min-h-screen bg-white">
      <nav className="border-b border-gray-200">
        <div className="container mx-auto px-4 py-2 flex justify-between items-center">
          <h1 className="text-xl font-bold">HR System</h1>
          <div className="flex items-center gap-4">
            <span className="text-gray-600">{userData.name}</span>
            <button onClick={handleLogout} className="secondary">
              Sign Out
            </button>
          </div>
        </div>
      </nav>

      <main className="container mx-auto px-4 py-6">
        <div className="max-w-3xl mx-auto grid grid-cols-2 gap-4">
          {/* Attendance Tile */}
          <div className="card hover:shadow-lg transition-shadow cursor-pointer">
            <div className="p-4">
              <div className="flex items-center justify-center w-12 h-12 bg-gray-100 rounded-full mb-3">
                <svg xmlns="http://www.w3.org/2000/svg" className="h-6 w-6 text-gray-600" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 8v4l3 3m6-3a9 9 0 11-18 0 9 9 0 0118 0z" />
                </svg>
              </div>
              <h2 className="text-lg font-bold mb-1">Attendance</h2>
              <p className="text-sm text-gray-600">Mark your attendance and view history</p>
            </div>
          </div>

          {/* Leave Management Tile */}
          <div className="card hover:shadow-lg transition-shadow cursor-pointer">
            <div className="p-4">
              <div className="flex items-center justify-center w-12 h-12 bg-gray-100 rounded-full mb-3">
                <svg xmlns="http://www.w3.org/2000/svg" className="h-6 w-6 text-gray-600" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M8 7V3m8 4V3m-9 8h10M5 21h14a2 2 0 002-2V7a2 2 0 00-2-2H5a2 2 0 00-2 2v12a2 2 0 002 2z" />
                </svg>
              </div>
              <h2 className="text-lg font-bold mb-1">Leave Management</h2>
              <p className="text-sm text-gray-600">Apply for leave and track balance</p>
            </div>
          </div>
        </div>
      </main>
    </div>
  );
};

export default Welcome;
