import React, { useState, useEffect } from 'react';
import DatePicker from 'react-datepicker';
import 'react-datepicker/dist/react-datepicker.css';
import './LeaveManagement.css';
import Layout from './Layout';
import LeaveRequestPopup from './LeaveRequestPopup';

interface User {
  id: number;
  name: string;
  email: string;
  role_id: number;
  department_id: number;
}

interface LeaveRequest {
  id: number;
  userId: number;
  startDate: string;
  endDate: string;
  leaveType: string;
  status: string;
}

interface LeaveManagementProps {
  userData: User | null;
  onLogout: () => void;
}

const LeaveManagement: React.FC<LeaveManagementProps> = ({ userData, onLogout }) => {
  const [selectedDate, setSelectedDate] = useState<Date | null>(new Date());
  const [isPopupOpen, setIsPopupOpen] = useState(false);
  const [leaveRequests, setLeaveRequests] = useState<LeaveRequest[]>([]);
  const [users, setUsers] = useState<User[]>([]);
  const [filteredUsers, setFilteredUsers] = useState<User[]>([]);

  useEffect(() => {
    if (userData) {
      console.log('Current user:', userData);
      fetchUsers();
      fetchLeaveRequests();
    }
  }, [userData]);

  useEffect(() => {
    // Filter users based on logged-in user's role
    if (userData && users.length > 0) {
      console.log('All users:', users);
      if (userData.role_id === 2) { // HR Manager
        // Show only employees (role_id: 3)
        const employeeUsers = users.filter(user => user.role_id === 3);
        console.log('Filtered employee users:', employeeUsers);
        setFilteredUsers(employeeUsers);
      } else {
        // For other roles, only show themselves
        setFilteredUsers([userData]);
      }
    }
  }, [userData, users]);

  const fetchUsers = async () => {
    try {
      const response = await fetch('http://localhost:5000/api/users');
      if (response.ok) {
        const data = await response.json();
        console.log('Fetched users:', data);
        setUsers(data);
      } else {
        console.error('Failed to fetch users:', response.status);
      }
    } catch (error) {
      console.error('Error fetching users:', error);
    }
  };

  const fetchLeaveRequests = async () => {
    try {
      const response = await fetch('http://localhost:5000/api/leave-requests');
      if (response.ok) {
        const data = await response.json();
        setLeaveRequests(data);
      }
    } catch (error) {
      console.error('Error fetching leave requests:', error);
      setLeaveRequests([]);
    }
  };

  const handleNewLeave = () => {
    setIsPopupOpen(true);
  };

  const handleSubmitLeave = async (leaveData: any) => {
    try {
      const response = await fetch('http://localhost:5000/api/leave-requests', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(leaveData),
      });

      if (response.ok) {
        fetchLeaveRequests();
      }
    } catch (error) {
      console.error('Error submitting leave request:', error);
    }
  };

  const isDateInLeaveRange = (date: Date) => {
    if (!Array.isArray(leaveRequests)) return false;
    
    return leaveRequests.some(request => {
      const startDate = new Date(request.startDate);
      const endDate = new Date(request.endDate);
      return date >= startDate && date <= endDate;
    });
  };

  const dayClassName = (date: Date) => {
    return isDateInLeaveRange(date) ? 'leave-day' : '';
  };

  return (
    <Layout userData={userData} onLogout={onLogout}>
      <div className="leave-container">
        <div className="leave-header">
          <h1>Leave Management</h1>
          <button className="new-leave-button" onClick={handleNewLeave}>
            <svg className="button-icon" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
              <path d="M12 4v16m8-8H4" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
            </svg>
            New Leave
          </button>
        </div>
        <div className="leave-content">
          <div className="date-picker-container">
            <DatePicker
              selected={selectedDate}
              onChange={setSelectedDate}
              inline
              className="custom-calendar"
              dayClassName={dayClassName}
            />
          </div>
          <div className="leave-details">
            <div className="leave-info">
              <h2>Leave Details</h2>
              {leaveRequests.length > 0 ? (
                leaveRequests.map(request => (
                  <div key={request.id} className="leave-request">
                    <p>User: {users.find(u => u.id === request.userId)?.name}</p>
                    <p>Type: {request.leaveType}</p>
                    <p>Status: {request.status}</p>
                    <p>Dates: {new Date(request.startDate).toLocaleDateString()} - {new Date(request.endDate).toLocaleDateString()}</p>
                  </div>
                ))
              ) : (
                <p className="no-leave-data">No leave requests found</p>
              )}
            </div>
          </div>
        </div>
      </div>
      <LeaveRequestPopup
        isOpen={isPopupOpen}
        onClose={() => setIsPopupOpen(false)}
        onSubmit={handleSubmitLeave}
        currentUser={userData!}
        users={filteredUsers}
      />
    </Layout>
  );
};

export default LeaveManagement;
