import React from 'react';
import { useNavigate } from 'react-router-dom';
import './Welcome.css';

interface UserData {
  name: string;
  email: string;
  role_id: number;
}

interface WelcomeProps {
  onLogout: () => void;
  userData: UserData | null;
}

const Welcome: React.FC<WelcomeProps> = ({ onLogout, userData }) => {
  const navigate = useNavigate();

  const handleLogout = () => {
    onLogout();
    navigate('/');
  };

  if (!userData) {
    return null;
  }

  return (
    <div className="min-h-screen bg-neutral-100">
      <header className="header">
        <div className="header-container">
          <h1 className="header-title">HR Systems</h1>
          <div className="header-right">
            <span className="username">{userData.name}</span>
            <button onClick={handleLogout} className="sign-out-btn">
              Sign Out
            </button>
          </div>
        </div>
      </header>

      {/* Main Content */}
      <main className="container mx-auto">
        <div className="tiles-container">
          {/* Attendance Tile */}
          <div className="tile" onClick={() => navigate('/attendance')} role="button" tabIndex={0}>
            <svg className="tile-icon" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
              <rect x="3" y="4" width="18" height="18" rx="2" stroke="#3B82F6" strokeWidth="2"/>
              <path d="M16 2v4M8 2v4M3 10h18" stroke="#3B82F6" strokeWidth="2" strokeLinecap="round"/>
              <circle cx="16" cy="16" r="2" fill="#3B82F6"/>
            </svg>
            <span className="tile-title">Attendance</span>
          </div>

          {/* Employees Tile */}
          <div className="tile">
            <svg className="tile-icon" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
              <path d="M17 21v-2a4 4 0 0 0-4-4H5a4 4 0 0 0-4 4v2" stroke="#10B981" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
              <circle cx="9" cy="7" r="4" stroke="#10B981" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
              <path d="M23 21v-2a4 4 0 0 0-3-3.87" stroke="#10B981" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
              <path d="M16 3.13a4 4 0 0 1 0 7.75" stroke="#10B981" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
            </svg>
            <span className="tile-title">Employees</span>
          </div>

          {/* Leave Management Tile */}
          <div className="tile">
            <svg className="tile-icon" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
              <path d="M12 6v6l4 2" stroke="#8B5CF6" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
              <path d="M12 22c5.523 0 10-4.477 10-10S17.523 2 12 2 2 6.477 2 12s4.477 10 10 10z" stroke="#8B5CF6" strokeWidth="2"/>
            </svg>
            <span className="tile-title">Leave Management</span>
          </div>
        </div>
      </main>
    </div>
  );
};

export default Welcome;
