import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

interface UserData {
  name: string;
  email: string;
  role_id: number;
}

interface WelcomeProps {
  onLogout: () => void;
  userData: UserData | null;
}

const Welcome: React.FC<WelcomeProps> = ({ onLogout, userData }) => {
  const navigate = useNavigate();
  const [showLogout, setShowLogout] = useState(false);

  const handleLogout = () => {
    onLogout();
    navigate('/');
  };

  const getRoleName = (roleId: number) => {
    switch (roleId) {
      case 1:
        return 'Admin';
      case 2:
        return 'HR Manager';
      case 3:
        return 'Employee';
      default:
        return 'Unknown';
    }
  };

  if (!userData) {
    return null;
  }

  return (
    <div className="h-screen flex flex-col bg-neutral-200">
      {/* Header */}
      <header className="bg-white shadow-md relative">
        <div className="container mx-auto px-6 py-4 flex justify-between items-center">
          <h1 className="text-2xl font-bold text-center flex-1">HR Systems</h1>
          <div className="relative">
            <button 
              onClick={() => setShowLogout(!showLogout)}
              className="flex flex-col items-center"
            >
              <div className="w-10 h-10 bg-neutral-800 rounded-full flex items-center justify-center mb-1">
                <svg className="w-6 h-6 text-white" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M16 7a4 4 0 11-8 0 4 4 0 018 0zM12 14a7 7 0 00-7 7h14a7 7 0 00-7-7z" />
                </svg>
              </div>
              <span className="text-sm text-neutral-800">{userData.name}</span>
            </button>
            {showLogout && (
              <button
                onClick={handleLogout}
                className="absolute right-0 mt-1 px-4 py-2 bg-white shadow-lg rounded-md text-sm text-neutral-800 hover:bg-neutral-100"
              >
                Sign Out
              </button>
            )}
          </div>
        </div>
      </header>

      {/* Main Content */}
      <main className="flex-1 flex items-center justify-center p-6">
        <div className="flex gap-8">
          {/* Leave Management Tile */}
          <div className="w-[250px] rounded-2xl bg-neutral-800 text-white p-8 flex flex-col items-center justify-center cursor-pointer hover:bg-neutral-700 transition-colors">
            <div className="w-16 h-16 mb-6">
              <svg className="w-full h-full" viewBox="0 0 24 24" fill="none" stroke="currentColor">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M8 7V3m8 4V3m-9 8h10M5 21h14a2 2 0 002-2V7a2 2 0 00-2-2H5a2 2 0 00-2 2v12a2 2 0 002 2z" />
              </svg>
            </div>
            <h2 className="text-lg font-medium mb-1">Leave Management</h2>
          </div>

          {/* Attendance Tile */}
          <div className="w-[250px] rounded-2xl bg-neutral-800 text-white p-8 flex flex-col items-center justify-center cursor-pointer hover:bg-neutral-700 transition-colors">
            <div className="w-16 h-16 mb-6">
              <svg className="w-full h-full" viewBox="0 0 24 24" fill="none" stroke="currentColor">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 8v4l3 3m6-3a9 9 0 11-18 0 9 9 0 0118 0z" />
              </svg>
            </div>
            <h2 className="text-lg font-medium mb-1">Attendance</h2>
          </div>
        </div>
      </main>
    </div>
  );
};

export default Welcome;
