import React from 'react';
import { useNavigate } from 'react-router-dom';

interface UserData {
  name: string;
  email: string;
  role_id: number;
}

interface WelcomeProps {
  onLogout: () => void;
  userData: UserData | null;
}

const Welcome: React.FC<WelcomeProps> = ({ onLogout, userData }) => {
  const navigate = useNavigate();

  const handleLogout = () => {
    onLogout();
    navigate('/');
  };

  if (!userData) {
    return null;
  }

  return (
    <div className="min-h-screen bg-neutral-100">
      {/* Navigation Bar */}
      <nav className="bg-neutral-900 text-white py-4 px-6">
        <div className="container mx-auto flex items-center justify-between">
          <h1 className="text-xl font-medium">HR Systems</h1>
          <div className="flex items-center gap-6">
            <span className="text-sm">{userData.name}</span>
            <button
              onClick={handleLogout}
              className="px-4 py-2 text-sm bg-neutral-800 hover:bg-neutral-700 rounded-lg transition-colors"
            >
              Sign Out
            </button>
          </div>
        </div>
      </nav>

      {/* Main Content */}
      <main className="container mx-auto py-12">
        <div className="flex justify-center gap-24">
          <button
            className="w-80 h-48 bg-neutral-900 text-white rounded-xl hover:bg-neutral-800 transition-colors text-base"
          >
            Leave Management
          </button>
          <button
            className="w-80 h-48 bg-neutral-900 text-white rounded-xl hover:bg-neutral-800 transition-colors text-base"
          >
            Attendance
          </button>
        </div>
      </main>
    </div>
  );
};

export default Welcome;
