import React from 'react';
import { useNavigate } from 'react-router-dom';

interface UserData {
  name: string;
  email: string;
  role_id: number;
}

interface WelcomeProps {
  onLogout: () => void;
  userData: UserData | null;
}

const Welcome: React.FC<WelcomeProps> = ({ onLogout, userData }) => {
  const navigate = useNavigate();

  const handleLogout = () => {
    onLogout();
    navigate('/');
  };

  const getRoleName = (roleId: number) => {
    switch (roleId) {
      case 1:
        return 'Admin';
      case 2:
        return 'HR Manager';
      case 3:
        return 'Employee';
      default:
        return 'Unknown';
    }
  };

  if (!userData) {
    return null;
  }

  return (
    <div className="min-h-screen bg-white">
      <nav className="border-b border-gray-200">
        <div className="container mx-auto px-4 py-2 flex justify-between items-center">
          <h1 className="text-xl font-bold">HR System</h1>
          <div className="flex items-center gap-4">
            <span className="text-gray-600">{userData.name}</span>
            <button onClick={handleLogout} className="secondary">
              Sign Out
            </button>
          </div>
        </div>
      </nav>

      <main className="container mx-auto px-4 py-6">
        <div className="max-w-lg mx-auto flex justify-center gap-8">
          {/* Attendance Icon */}
          <div className="flex flex-col items-center cursor-pointer group">
            <div className="w-16 h-16 flex items-center justify-center bg-gray-100 rounded-lg mb-2 group-hover:bg-gray-200 transition-colors">
              <svg xmlns="http://www.w3.org/2000/svg" className="h-8 w-8 text-gray-600" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 8v4l3 3m6-3a9 9 0 11-18 0 9 9 0 0118 0z" />
              </svg>
            </div>
            <span className="text-sm font-medium text-gray-700">Attendance</span>
          </div>

          {/* Leave Management Icon */}
          <div className="flex flex-col items-center cursor-pointer group">
            <div className="w-16 h-16 flex items-center justify-center bg-gray-100 rounded-lg mb-2 group-hover:bg-gray-200 transition-colors">
              <svg xmlns="http://www.w3.org/2000/svg" className="h-8 w-8 text-gray-600" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M8 7V3m8 4V3m-9 8h10M5 21h14a2 2 0 002-2V7a2 2 0 00-2-2H5a2 2 0 00-2 2v12a2 2 0 002 2z" />
              </svg>
            </div>
            <span className="text-sm font-medium text-gray-700">Leave Management</span>
          </div>
        </div>
      </main>
    </div>
  );
};

export default Welcome;
