import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';

interface WelcomeProps {
  onLogout: () => void;
}

interface UserData {
  id: number;
  name: string;
  email: string;
  role_id: number;
  department_id: number | null;
}

const Welcome: React.FC<WelcomeProps> = ({ onLogout }) => {
  const [userData, setUserData] = useState<UserData | null>(null);
  const [error, setError] = useState('');
  const [isLoading, setIsLoading] = useState(true);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchUserData = async () => {
      try {
        const token = localStorage.getItem('token');
        if (!token) {
          throw new Error('No token found');
        }

        const response = await fetch('http://localhost:8000/users/me', {
          headers: {
            'Authorization': `Bearer ${token}`
          }
        });

        if (!response.ok) {
          throw new Error('Failed to fetch user data');
        }

        const data = await response.json();
        setUserData(data);
      } catch (err) {
        setError('Failed to load user data');
        console.error('Error fetching user data:', err);
      } finally {
        setIsLoading(false);
      }
    };

    fetchUserData();
  }, []);

  const handleLogout = () => {
    onLogout();
    navigate('/');
  };

  const getRoleName = (roleId: number) => {
    switch (roleId) {
      case 1:
        return 'Admin';
      case 2:
        return 'HR Manager';
      case 3:
        return 'Employee';
      default:
        return 'Unknown';
    }
  };

  return (
    <div className="min-h-screen bg-white">
      <nav className="border-b border-gray-200">
        <div className="container mx-auto px-4 py-4 flex justify-between items-center">
          <h1 className="text-xl font-bold">HR System</h1>
          <button onClick={handleLogout} className="secondary">
            Sign Out
          </button>
        </div>
      </nav>

      <main className="container mx-auto px-4 py-8">
        <div className="card">
          <div className="mb-6">
            <h2 className="text-2xl font-bold mb-2">Welcome, {userData?.name || 'User'}</h2>
            <p className="text-gray-600">Here's your account information</p>
          </div>

          {error ? (
            <div className="alert alert-error">
              {error}
            </div>
          ) : isLoading ? (
            <div className="text-center py-8">
              <p className="text-gray-600">Loading user data...</p>
            </div>
          ) : userData ? (
            <div className="space-y-4">
              <div className="grid grid-cols-2 gap-4">
                <div>
                  <p className="text-sm text-gray-600">Email</p>
                  <p className="font-medium">{userData.email}</p>
                </div>
                <div>
                  <p className="text-sm text-gray-600">Role</p>
                  <p className="font-medium">{getRoleName(userData.role_id)}</p>
                </div>
              </div>
            </div>
          ) : null}
        </div>
      </main>
    </div>
  );
};

export default Welcome;
