import React from 'react';
import { useNavigate } from 'react-router-dom';

interface UserData {
  name: string;
  email: string;
  role_id: number;
}

interface WelcomeProps {
  onLogout: () => void;
  userData: UserData | null;
}

const Welcome: React.FC<WelcomeProps> = ({ onLogout, userData }) => {
  const navigate = useNavigate();

  const handleLogout = () => {
    onLogout();
    navigate('/');
  };

  const getRoleName = (roleId: number) => {
    switch (roleId) {
      case 1:
        return 'Admin';
      case 2:
        return 'HR Manager';
      case 3:
        return 'Employee';
      default:
        return 'Unknown';
    }
  };

  if (!userData) {
    return null;
  }

  return (
    <div className="h-screen bg-white flex flex-col">
      <nav className="border-b border-gray-200 flex-none">
        <div className="container mx-auto px-4 py-2 flex justify-between items-center">
          <h1 className="text-xl font-bold">HR System</h1>
          <div className="flex items-center gap-4">
            <span className="text-gray-600">{userData.name}</span>
            <button onClick={handleLogout} className="secondary">
              Sign Out
            </button>
          </div>
        </div>
      </nav>

      <main className="flex-1 container mx-auto p-4 flex items-center justify-center bg-orange-50">
        <div className="w-full max-w-3xl grid grid-cols-2 gap-4">
          {/* Attendance Tile */}
          <div className="bg-blue-600 text-white p-5 rounded-lg cursor-pointer hover:bg-blue-700 transition-colors">
            <div className="flex items-start mb-3">
              <svg xmlns="http://www.w3.org/2000/svg" className="h-7 w-7" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 8v4l3 3m6-3a9 9 0 11-18 0 9 9 0 0118 0z" />
              </svg>
            </div>
            <h2 className="text-xl font-bold mb-1">Attendance</h2>
            <p className="text-blue-100 text-sm">Mark attendance & view history</p>
            <div className="mt-3">
              <div className="h-6 w-20">
                {/* Placeholder for potential stats/graph */}
              </div>
            </div>
          </div>

          {/* Leave Management Tile */}
          <div className="bg-indigo-100 text-gray-800 p-5 rounded-lg cursor-pointer hover:bg-indigo-200 transition-colors">
            <div className="flex items-start mb-3">
              <svg xmlns="http://www.w3.org/2000/svg" className="h-7 w-7" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M8 7V3m8 4V3m-9 8h10M5 21h14a2 2 0 002-2V7a2 2 0 00-2-2H5a2 2 0 00-2 2v12a2 2 0 002 2z" />
              </svg>
            </div>
            <h2 className="text-xl font-bold mb-1">Leave Management</h2>
            <p className="text-gray-600 text-sm">Apply & track leave balance</p>
            <div className="mt-3">
              <div className="h-6 w-20">
                {/* Placeholder for potential stats/graph */}
              </div>
            </div>
          </div>
        </div>
      </main>
    </div>
  );
};

export default Welcome;
