import React from 'react';
import { useNavigate } from 'react-router-dom';

interface UserData {
  name: string;
  email: string;
  role_id: number;
}

interface WelcomeProps {
  onLogout: () => void;
  userData: UserData | null;
}

const Welcome: React.FC<WelcomeProps> = ({ onLogout, userData }) => {
  const navigate = useNavigate();

  const handleLogout = () => {
    onLogout();
    navigate('/');
  };

  if (!userData) {
    return null;
  }

  return (
    <div className="min-h-screen bg-neutral-100">
      <header className="bg-neutral-900 text-white">
        <div className="container mx-auto px-6">
          {/* Top bar with logo and user info */}
          <div className="h-16 flex items-center justify-between border-b border-neutral-800">
            <h1 className="text-xl font-medium">HR Systems</h1>
            <div className="flex items-center gap-6">
              <span className="text-sm">{userData.name}</span>
              <button
                onClick={handleLogout}
                className="px-4 py-2 text-sm bg-neutral-800 hover:bg-neutral-700 rounded-lg transition-colors"
              >
                Sign Out
              </button>
            </div>
          </div>

          {/* Navigation links */}
          <nav className="h-12 flex items-center">
            <div className="flex gap-8">
              <a href="/attendance" className="text-sm text-neutral-300 hover:text-white transition-colors">
                Attendance
              </a>
              <a href="/leave-management" className="text-sm text-neutral-300 hover:text-white transition-colors">
                Leave Management
              </a>
            </div>
          </nav>
        </div>
      </header>

      {/* Main Content */}
      <main className="container mx-auto py-8">
        {/* Content will go here */}
      </main>
    </div>
  );
};

export default Welcome;
