import React from 'react';
import { useNavigate } from 'react-router-dom';

interface UserData {
  name: string;
  email: string;
  role_id: number;
}

interface WelcomeProps {
  onLogout: () => void;
  userData: UserData | null;
}

const Welcome: React.FC<WelcomeProps> = ({ onLogout, userData }) => {
  const navigate = useNavigate();

  const handleLogout = () => {
    onLogout();
    navigate('/');
  };

  const getRoleName = (roleId: number) => {
    switch (roleId) {
      case 1:
        return 'Admin';
      case 2:
        return 'HR Manager';
      case 3:
        return 'Employee';
      default:
        return 'Unknown';
    }
  };

  if (!userData) {
    return null;
  }

  return (
    <div className="h-screen flex flex-col bg-white">
      {/* Header */}
      <header className="h-14 bg-white border-b border-gray-200 flex-none">
        <div className="h-full container mx-auto px-6 flex justify-between items-center">
          <h1 className="text-xl font-bold">HR System</h1>
          <div className="flex items-center gap-6">
            <span className="text-gray-600">{userData.name}</span>
            <button onClick={handleLogout} className="secondary">
              Sign Out
            </button>
          </div>
        </div>
      </header>

      {/* Main Content */}
      <main className="flex-1 bg-gray-100 flex items-center justify-center">
        <div className="flex gap-6">
          {/* Attendance Tile */}
          <div className="w-[250px] h-[250px] bg-white rounded-lg shadow-sm hover:shadow-md transition-shadow cursor-pointer p-8 flex flex-col items-center justify-center">
            <div className="w-20 h-20 mb-4">
              <svg className="w-full h-full text-gray-700" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 8v4l3 3m6-3a9 9 0 11-18 0 9 9 0 0118 0z" />
              </svg>
            </div>
            <h2 className="text-lg font-semibold text-gray-900">Attendance</h2>
            <p className="text-sm text-gray-500 text-center mt-2">Mark attendance & view history</p>
          </div>

          {/* Leave Management Tile */}
          <div className="w-[250px] h-[250px] bg-white rounded-lg shadow-sm hover:shadow-md transition-shadow cursor-pointer p-8 flex flex-col items-center justify-center">
            <div className="w-20 h-20 mb-4">
              <svg className="w-full h-full text-gray-700" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M8 7V3m8 4V3m-9 8h10M5 21h14a2 2 0 002-2V7a2 2 0 00-2-2H5a2 2 0 00-2 2v12a2 2 0 002 2z" />
              </svg>
            </div>
            <h2 className="text-lg font-semibold text-gray-900">Leave Management</h2>
            <p className="text-sm text-gray-500 text-center mt-2">Apply & track leave balance</p>
          </div>
        </div>
      </main>
    </div>
  );
};

export default Welcome;
